(186, 'argon_footer_html', '<style>\r\n/* 核心样式 */\r\n.github-badge {\r\n  display: inline-block;\r\n  border-radius: 4px;\r\n  text-shadow: none;\r\n  font-size: 13.1px;\r\n  color: #fff;\r\n  line-height: 15px;\r\n  margin-bottom: 5px;\r\n  font-family: \"Open Sans\", sans-serif;\r\n}\r\n.github-badge .badge-subject {\r\n  display: inline-block;\r\n  background-color: #4d4d4d;\r\n  padding: 4px 4px 4px 6px;\r\n  border-top-left-radius: 4px;\r\n  border-bottom-left-radius: 4px;\r\n}\r\n.github-badge .badge-value {\r\n  display: inline-block;\r\n  padding: 4px 6px 4px 4px;\r\n  border-top-right-radius: 4px;\r\n  border-bottom-right-radius: 4px;\r\n}\r\n.github-badge-big {\r\n  display: inline-block;\r\n  border-radius: 6px;\r\n  text-shadow: none;\r\n  font-size: 14.1px;\r\n  color: #fff;\r\n  line-height: 18px;\r\n  margin-bottom: 7px;\r\n}\r\n.github-badge-big .badge-subject {\r\n  display: inline-block;\r\n  background-color: #4d4d4d;\r\n  padding: 4px 4px 4px 6px;\r\n  border-top-left-radius: 4px;\r\n  border-bottom-left-radius: 4px;\r\n}\r\n.github-badge-big .badge-value {\r\n  display: inline-block;\r\n  padding: 4px 6px 4px 4px;\r\n  border-top-right-radius: 4px;\r\n  border-bottom-right-radius: 4px;\r\n}\r\n.bg-orange { background-color: #ec8a64 !important; }\r\n.bg-red { background-color: #cb7574 !important; }\r\n.bg-apricots { background-color: #f7c280 !important; }\r\n.bg-casein { background-color: #dfe291 !important; }\r\n.bg-shallots { background-color: #97c3c6 !important; }\r\n.bg-ogling { background-color: #95c7e0 !important; }\r\n.bg-haze { background-color: #9aaec7 !important; }\r\n.bg-mountain-terrier { background-color: #99a5cd !important; }\r\n.bg-green { background-color: #72c26e !important; }\r\n</style>\r\n\r\n<!-- 备案信息 -->\r\n<div class=\"github-badge-big\">\r\n  <span class=\"badge-subject\"><i class=\"fa fa-id-card\"></i> 备案号</span>\r\n  <span class=\"badge-value bg-orange\">\r\n    <a href=\"https://beian.miit.gov.cn/\" target=\"_blank\">苏ICP备0000000000号</a> |\r\n    <a href=\"http://www.beian.gov.cn/portal/registerSystemInfo?recordcode=32072202010255\" target=\"_blank\">苏公网安备 00000000000000号</a>\r\n  </span>\r\n</div>\r\n\r\n<!-- CDN & WordPress -->\r\n<div class=\"github-badge-big\">\r\n  <span class=\"badge-subject\"><i class=\"fa fa-cloud\"></i> CDN</span>\r\n  <span class=\"badge-value bg-shallots\">\r\n    <a href=\"https://www.upyun.com/\" target=\"_blank\">Upyun</a>\r\n  </span>\r\n  <span class=\"badge-subject\"><i class=\"fa fa-wordpress\"></i> Powered</span>\r\n  <span class=\"badge-value bg-green\">\r\n    <a href=\"https://cn.wordpress.org/\" target=\"_blank\">WordPress</a>\r\n  </span>\r\n</div>\r\n\r\n<!-- 自动年份版权 -->\r\n<div class=\"github-badge-big\">\r\n  <span class=\"badge-subject\">Copyright</span>\r\n  <span class=\"badge-value bg-red\">\r\n    <span id=\"copyright-year\"></span> <i class=\"fa fa-copyright\"></i> Fawang\r\n  </span>\r\n</div>\r\n\r\n<!-- 运行时间 -->\r\n<div class=\"github-badge-big\">\r\n  <span class=\"badge-subject\"><i class=\"fa fa-clock-o\"></i> Running Time</span>\r\n  <span class=\"badge-value bg-apricots\">\r\n    <span id=\"blog_running_days\" class=\"odometer odometer-auto-theme\"></span> days\r\n    <span id=\"blog_running_hours\" class=\"odometer odometer-auto-theme\"></span> H\r\n    <span id=\"blog_running_mins\" class=\"odometer odometer-auto-theme\"></span> M\r\n    <span id=\"blog_running_secs\" class=\"odometer odometer-auto-theme\"></span> S\r\n  </span>\r\n</div>\r\n\r\n<script>\r\n// 自动更新年份\r\n(function() {\r\n  const startYear = 2022;\r\n  const currentYear = new Date().getFullYear();\r\n  const text = startYear === currentYear ? `${startYear}` : `${startYear}-${currentYear}`;\r\n  document.getElementById(\"copyright-year\").innerText = text;\r\n})();\r\n\r\n// 正确的运行时间逻辑（起始时间 2024-06-01）\r\nvar blog_running_days = document.getElementById(\"blog_running_days\");\r\nvar blog_running_hours = document.getElementById(\"blog_running_hours\");\r\nvar blog_running_mins = document.getElementById(\"blog_running_mins\");\r\nvar blog_running_secs = document.getElementById(\"blog_running_secs\");\r\n\r\nfunction refresh_blog_running_time() {\r\n  var start = new Date(\"2025-06-01T00:00:00\");\r\n  var now = new Date();\r\n  var time = now - start;\r\n  var d = parseInt(time / (24 * 60 * 60 * 1000));\r\n  var h = parseInt((time % (24 * 60 * 60 * 1000)) / (60 * 60 * 1000));\r\n  var m = parseInt((time % (60 * 60 * 1000)) / (60 * 1000));\r\n  var s = parseInt((time % (60 * 1000)) / 1000);\r\n  blog_running_days.innerHTML = d;\r\n  blog_running_hours.innerHTML = h;\r\n  blog_running_mins.innerHTML = m;\r\n  blog_running_secs.innerHTML = s;\r\n}\r\nrefresh_blog_running_time();\r\nif (typeof bottomTimeIntervalHasSet === \"undefined\") {\r\n  var bottomTimeIntervalHasSet = true;\r\n  setInterval(refresh_blog_running_time, 1000);\r\n}\r\n</script>\r\n\r\n<!-- Live2D 插件 -->\r\n<script src=\"/wp-content/themes/argon/argon/live2d/TweenLite.js\"></script>\r\n<script src=\"/wp-content/themes/argon/argon/live2d/live2dcubismcore.min.js\"></script>\r\n<script src=\"/wp-content/themes/argon/argon/live2d/pixi.min.js\"></script>\r\n<script src=\"/wp-content/themes/argon/argon/live2d/cubism4.min.js\"></script>\r\n<link href=\"/wp-content/themes/argon/argon/live2d/pio.css\" rel=\"stylesheet\" type=\"text/css\" />\r\n<script src=\"/wp-content/themes/argon/argon/live2d/pio.js\"></script>\r\n<script src=\"/wp-content/themes/argon/argon/live2d/pio_sdk4.js\"></script>\r\n<script src=\"/wp-content/themes/argon/argon/live2d/load.js\"></script>\r\n', 'auto'),
